import React from 'react'
import { useForm } from 'react-hook-form';
import { zodResolver } from "@hookform/resolvers/zod";
import { string, z }  from "zod";
import './connect.css';

const schema = z.object({
  name: string().min(1, { message: 'Name is required!' }),
  email: string().email(),
  message: string().min(1, { message: 'Message is required!' }),
});

const Connect = ( { onSave, user = {} }) => {
  const { register, handleSubmit, formState } = useForm({ resolver: zodResolver(schema) });

  const { errors } = formState;

  const handleSave = (formValues) => {
    onSave(formValues);
  }

  return (
    <main className='connect-section'>
      <div className='connect-text'>
        <p className='connect-title'> Let's Connect! </p>
        <p className='connect-subtitle'>I WOULD LOVE TO CONNECT WITH YOU ABOUT A NEW OPPORTUNITY, A COLLABORATION, OR A QUICK CHAT ABOUT ANYTHING!</p>
        </div>

      <form className="connect-form" onSubmit={handleSubmit(handleSave)}>
        <div>
          <input
            className='name-input'
            placeholder='Name'
            {...register('name')}
          />
          <div>{errors.name?.message}</div>
        </div>

        <div>
          <input
            className='email-input'
            placeholder='Email'
            {...register('email')}
          />
          <div>{errors.email?.message}</div>
        </div>

        <div>
          <textarea
            className='message-input'
            placeholder='Message'
            {...register('message')}
          />
          <div>{errors.message?.message}</div>
        </div>
        <button type="submit">Connect</button>
      </form>    
    </main>
  )
}

export default Connect
