      setErrors(errors);
      return;
    }
    setErrors({});
    onSave(userData);
  }

  return (
    <main>
      <p> Let's Connect! </p>
      <p>I WOULD LOVE TO CONNECT WITH YOU ABOUT A NEW OPPORTUNITY, A COLLABORATION, OR A QUICK CHAT ABOUT ANYTHING!</p>

      <form onSubmit={handleSubmit(handleSave)}>
        <input
          placeholder="Name"
          {...register('name')}
        />
        <input
          placeholder="Email"
          {...register('email')}
        />
        <textarea
          placeholder="Message"
          {...register('message')}
        />
        <div> 
          <button type="submit">Connect</button>
          <p>
            {errors && <div>{errors.name}</div>}
            <br/>
            {errors && <div>{errors.name}</div>}
          </p>
        </div>
      </form>    
    </main>
  )
}

export default Connect
